.product-card[data-sku="1120"]::after,
.product-card[data-sku="1119"]::after {
    content: "FRETE GRÁTIS";
    display: block;
    position: absolute;
    top: 3rem;
    right: 0;
    padding: .25rem .5rem;
    background: var(--primary-lighter);
    color: #fff;
    font-size: 12px;
    font-weight: bold;
    border-radius: var(--border-radius);
}
.product-card:hover::after {
    border-bottom-right-radius: 0;
    border-top-right-radius: 0;
}
