css:
/* INÍCIO NOVO LAYOUT */

#bodyIndex .main {
    padding-top: 0;
}
#bodyIndex .page-slider {
    margin-bottom: 10px;
}
.nav-catalogue .header-navigation-content {
    background: #fffefc;
}
.divisoria {
    background-image: url('//cdn.e-com.club/s/www.artestore.com.br/uploads/divisor7.png');
    height: 57px;
    width: 100%
}
.pi-price span {
    color: #9c9c9c;
}
.pi-price {
    color: #d89a3e;
}
.style-text.text-default h3 {
    font-weight: bold;
    text-align: center;
}
.label {
    border-radius: 0;
}

/* topo */
.pre-header,
.pre-header a {
    color: #452f06;
    font-weight: 400;
}
.additional-shop-info li {
    border: none;
}
.additional-nav {
    text-transform: uppercase;
}
.additional-nav > ul > li:nth-last-child(-n+2) {
    display: none;
}
.additional-nav > ul > li:not(:first-child) {
    border: none;
    padding-right: 0;
}
.langs-block-others > a:not(:first-child):not(:last-child) {
    display: none;
}

/* rodapé */
.pre-footer-col h1,
.pre-footer-col h2,
.pre-footer-title {
    color: #381b1c;
}
#rodape_links {
    font-size: 11px;
}

/* whatsapp no menu */
.duvidas div:first-child {
    float: left;
}
.duvidas div:last-child {
    float: left;
    float: right;
    padding-top: 10px;
    padding-left: 5px;
    font-size: 20px;
}
.duvidas {
    float: right;
    padding-top: 11px;
    font-weight: 700;
}
p.p1 {
    float: left;   
    margin-bottom: 0;
    padding-top: 5px;
    color: #f3d95d;
}
p.p2 {
    float: left;
    color: #e8b79a;
}
p.whats-number {
    color: #b6c1bd;
}

/* carrinho no topo */
.top-header .top-cart-block {
    margin-top: 10px;
    text-transform: uppercase;
}
.top-header .top-cart-info-value {
    font-weight: 700;
}
.top-header .top-cart-block {
    padding: 4px 32px 0 0;
}
.top-header .top-cart-block .fa-shopping-cart {
    padding: 0;
    background: none;
}
.top-header .top-cart-block .fa-shopping-cart::before {
    content: " ";
    height: 25px;
    width: 25px;
    background-image: url('//cdn.e-com.club/s/www.artestore.com.br/uploads/novo-layout/cart-icon.png');
    background-repeat: no-repeat;
    display: block;
}
.top-header .user i {
    display: none;
}

/* oferta no fim do menu */
/* esconder no início */
.header .nav-catalogue:first-child {
    display: none;
}

/* barra de busca */
#fbusca .input-group {
    border: 1px solid #c6bdaf;
}
#fbusca .input-group > input {
    background: #faf2e4;
    font-weight: 200;
}
#fbusca .btn {
    background: #faf2e4;
    color: #ef8d66;
}

/* itens */
.pi-img-wrapper {
    border: 1px solid #ccc;
}

/* tarjas novo e oferta */
.sticker {
    border-radius: 0px;
    top: 0;
}
.sticker-sale {
    background: url('//cdn.e-com.club/s/www.artestore.com.br/uploads/novo-layout/oferta.png') no-repeat;
    height: 52px;
    width: 26px;
    right: 50px !important;
    left:initial;
}
.sticker-new {
    background: url('//cdn.e-com.club/s/www.artestore.com.br/uploads/novo-layout/novo.png') no-repeat;
    height: 52px;
    width: 26px;
    margin-right: 10px;
}

/* slider */
.carousel-slider .carousel-control .fa {
    background: none;
    width: auto;
    height: auto;
    margin-top: -32px;
}
.carousel-slider .carousel-control .fa::before {
    content: " ";
    display: block;
    height: 105px;
    width: 60px;
}
.carousel-slider .carousel-control.right .fa::before {
    background: url('//cdn.e-com.club/s/www.artestore.com.br/uploads/novo-layout/arrow-right.png') no-repeat;
}
.carousel-slider .carousel-control.left .fa::before {
    background: url('//cdn.e-com.club/s/www.artestore.com.br/uploads/novo-layout/arrow-left.png') no-repeat;
}
.carousel-control {
    opacity: 1;
}

/* ajustes página inicial */
#bloco_news .style-text > span {
    color: #696555;
    font-weight: 700;
    text-align: center;
    font-size: 26px;
    margin-top: 13px;
    display: inline-block;
}

/* produto */
.product-page {
    background: none;
    padding: 25px 0;
    border-top: 1px solid #eaeaea;
}
.product-main-image {
    width: 100%;
    max-width: none;
    padding: 20px;
    border: 1px solid #ccc;
    background: #fff;
}
#product-img-big {
    max-width: 500px;
    margin: 0 auto;
}
.product-page h1 {
    color: #696555;
    font-weight: 700;
    border-color: #eaeaea;
}
.product-page-options,
.price-availability-block {
    border-color: #eaeaea;
}
#preco_principal strong {
    color: #ef8b5e;
}
.quantity-down,
.quantity-up {
    background: none !important;
}
.product-page-cart .btn {
    background: #96c654;
    border: none;
}
.product-page-cart .btn.btn-lg {
    background: #de6d39;
}
.panel.parcel, .panel.parcel .panel-heading {
    background: white;
}
#parcelas_principal,
#desconto_principal {
    font-weight: 700;
}
#parcelas_principal:last-child:nth-child(2) {
    margin-left: 20px;
    display: inline-block;
}
.produtct-page .panel.parcel,
.produtct-page .panel.parcel .panel-heading {
    background: white;
}
.ml > img{
   max-width:100%;
}
.ml {
    margin-bottom: 20px;
    width:auto;
}

/*descrição do produto */
.nav-tabs>li.active>a:hover {
    background-color: #faf2e4;
    color: #978e7f;
}
.nav-tabs>li.active>a{
    background: #faf2e4 !important;
    color: #686454; !important;
    border-color: transparent !important;
    font-weight:bold;
}
.nav-tabs>li.active{
    border-bottom:1px solid #ccc;
}
.nav-tabs {
    background-color: #faf2e4;
    border-color: #ccc;
}
.product-page-content {
   padding:0;
   border: 1px solid #ccc;
   margin-top: 25px
}
#myTabContent{
   background:white;
}
.linha-descriçao {
    height: 2px;
    background: #686454;
    margin-left: 10px;
    margin-bottom: 10px;
}

/* títulos dos plugins */
.grid-title {
    color: #696555;
    font-weight: 700;
    font-size: 26px;
    margin-top: 45px;
}

/* div frete-grátis */
#frg {
    position: absolute;
    z-index: 2;
    top: 10px;
    left: 25px;
}
#frg .label {
    color: #d10057;
    font-size: 17px;
    background: none;
}

/* newsletter */ 
.padding-big {
    background:white;
    border:1px dashed #ccc;
}

#bloco_news .input-group {
    border-bottom: 1px solid black;
    padding-bottom:1px;
    margin-top: 10px;
}

#bloco_news input.form-control {
    border: transparent;
    background: white;
}

#bloco_news .input-group button.btn.btn-primary {
    background: white;
    color: black;
    border: transparent;
}
#bloco_news .style-text span {
    color: #95b6b0!important;
}
.linha-newsletter {
    height: 2px;
    width: 60px;
    background: #da9a3e;
    margin-top: 10px;
}

/* categoria */ 

.sidebar .list-group-item {
    background: white;
}
.sidebar .list-group-item .fa-angle-right:before {
    content: " -- "; 
    color: black;
}
.select-styled {
    background-color: #ececec;
}
.pagination>li>a, 
.pagination>li>span,
.pagination>li:last-child>a {
    border-radius: 16px !important;
}

#bodyCategory .main > .container > .row > .col-md-3:empty {
    display: none;
}
#bodyCategory .main > .container > .row > .col-md-3:empty+div{
    width: 100%;
}

#bloco_filtros .sidebar li {
    border-bottom: 1px dashed #ccc;
}

div#xFiltro {
    border: none;
}

#xFiltro > span {
    font-size: 15px;
}
#xFiltro > big {
    font-size: 14px;
    font-weight: 300;
}

#bloco_filtros .sidebar li h3 {
    font-size:13px;
}
 custom.css #peso_dimensoes {display: none;}

/* FIM NOVO LAYOUT */
